window.addEventListener("scroll",()=>{document.querySelector("header").classList.toggle("sticky",window.scrollY>0)});const hamburger=document.querySelector(".hamburger"),menuUl=document.querySelector("header ul.main"),body=document.querySelector("body"),toggleMenuClassActive=()=>{hamburger.classList.toggle("active"),menuUl.classList.toggle("active"),body.classList.toggle("menu-active")};hamburger.addEventListener("click",toggleMenuClassActive);const dropdownMenuItems=document.querySelectorAll(".dropdown-big"),removeClass=(e,s)=>{e.classList.contains(s)&&e.classList.remove(s)},addClass=(e,s)=>{e.classList.contains(s)||e.classList.add(s)},setLiHeight=e=>{const s=e.querySelectorAll(".ddb-li-body"),t=Array.prototype.slice.call(s).map(e=>e.offsetHeight),o=Math.max(...t);s.forEach(e=>e.style.height=`${o}px`)},showDropdown=(e,s)=>{addClass(e,"active"),addClass(s,"active"),setLiHeight(e)},hideDropdown=(e,s)=>{e.classList.contains("mouse-in")||(removeClass(e,"active"),removeClass(s,"active"))},mouseInDropdown=(e,s)=>{e.addEventListener("mouseenter",()=>addClass(e,"mouse-in")),e.addEventListener("mouseleave",()=>{removeClass(e,"mouse-in"),removeClass(e,"active"),removeClass(s,"active")})};dropdownMenuItems.forEach(e=>{const s=document.querySelector(`#${e.getAttribute("data-for")}`);e.addEventListener("mouseenter",()=>showDropdown(s,e)),e.addEventListener("mouseleave",()=>mouseInDropdown(s,e)),e.addEventListener("mouseleave",()=>setTimeout(hideDropdown,300,s,e))});